import React, { useState } from 'react';
import { useChatContext } from 'stream-chat-react';

import {userList} from './'
import { CloseCreateChannel } from '../assets';
import UserList from './UserList';


const ChannelNameInput = ({channelName, setChannelName}) =>{


    const handlerChange = (event) => {

        event.preventDefault();
        setChannelName(event.target.value)
    }

    return (
        <div>
            <div className="channel-name-input__wrapper">
                <p>Name</p>
                <input value={channelName} onChange={handlerChange} placeholder="channel-name"/>
                <p>Add Members</p>
            </div>
        </div>
    )
}


const CreateChannel = ({createType, setIsCreating}) => {
    const {client, setActiveChannel} = useChatContext();

    const [selectedUsers, setSelectedUsers] = useState([client.userID || '']);

    const [channelName, setChannelName ] = useState('');

    return (
        <div className="create-channel__container">
            <div className="create-channel__header">
                <p>{createType === 'chat' ? 'Create new Channel': 'Send a Direct Message'}</p>
                <CloseCreateChannel setIsCreating={setIsCreating} />
            </div>
            {createType ==='chat' && <ChannelNameInput channelName={channelName} setChannelName={setChannelName}/>}
            <UserList setSelectedUsers={setSelectedUsers}/>
        </div>
    );
}

export default CreateChannel;
